styles.content:
self {
	background-color: rgb(61, 54, 61);
}

self .row {
  align-items: center;
}

self .header {
	font-weight: bold;
}

self .header .header-cell {
	background-color: transparent;
	border-top: none;
	border-bottom: none !important;
	color: #fff;
	font-size: 11px;
	text-transform: uppercase;
	letter-spacing: .2rem;
}

self .row {
	background-color: rgba(0, 0, 0, 0.1);
	color: #777;
	transition: 0.3s all ease;
}

self .row:hover {
	background-color: rgba(0, 0, 0, 0.1);
	color: white;
}

self .row:hover .col-firstname {
	color: #b474e4;
}

self .row.row-even {
	background-color: unset;
}

self .header,
self .header .resizable > :not([hidden]) ~ :not([hidden]),
self .row > :not([hidden]) ~ :not([hidden]),
self .Grid .innerScrollContainer,
self .Grid .innerScrollContainer > :not([hidden]) ~ :not([hidden]) {
  border-width: 0;
}
