styles.content:
self {
  background-color : #ffffffff;
  border-radius : 15px;
	box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
	max-width: 40%;
	min-width: 250px;
}

self {
  background-color : #4b4ba5;
  border-radius : 15px;
  display : flex;
  color : #ffffffff;
  flex-wrap : wrap;
  align-content : flex-start;
  justify-content : space-between;
  max-width : 350px;
  align-items : center;
  cursor : pointer;
	border-radius : 15px;
	box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
	max-width: 40%;
	min-width: 250px;
	opacity: 0.8;
  transition: 0.3s;
}

self:hover { opacity: 1;}

self {
  border-radius: 10px;
  box-shadow: 0 0 40px 0 rgb(0 0 0 / 15%);
}

self .header .header-cell {
  background-color: #4b4ba5;
  color: #fff;
  font-weight: bold;
  padding: 1.25rem 2rem;
}

self .row {
  align-items: center;
}

self .row .cell {
  padding: 0.5rem 2rem;
}

self .row .cell > span {
  justify-content: flex-start;
}

self .row.selected {
  background-color: #d2d7f5;
}

self .row-even:not(.selected):not(:hover) {
  background-color: #f8f6ff;
}

self .header .resizable > :not([hidden]) ~ :not([hidden]),
self .row > :not([hidden]) ~ :not([hidden]),
self .Grid .innerScrollContainer,
self .Grid .innerScrollContainer > :not([hidden]) ~ :not([hidden]) {
  border-width: 0;
}

self {
  display : flex;
  max-width : 490px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
