styles.content:
self {
  display : flex;
  align-items : end;
  border-width : 0px;
  flex-direction : column;
}

self {
  display : flex;
  justify-content : space-between;
}

self .py-2.px-3 {
	padding: 1px;
}

self .row {
  align-items: center;
}

self .col-id > span {
  justify-content: flex-start;
}

self .header {
	font-weight: bold;
}

self .header .header-cell {
	background-color: transparent;
	border-top: none;
	border-bottom: none !important;
	color: #fff;
	font-size: 11px;
	text-transform: uppercase;
	letter-spacing: .2rem;
}

self .row {
	background-color: rgba(0, 0, 0, 0.1);
	color: #777;
	transition: .3s all ease;
}

self .row:hover {
	color: white;
}

self .row:hover .col-time-spent {
	color: #fdd114;
}

self .row.row-even {
	background-color: unset;
}

self .header,
self .header .resizable > :not([hidden]) ~ :not([hidden]),
self .row > :not([hidden]) ~ :not([hidden]),
self .Grid .innerScrollContainer,
self .Grid .innerScrollContainer > :not([hidden]) ~ :not([hidden]) {
  border-width: 0;
}

self {
  writing-mode: vertical-lr;
	rotate: 180deg;
  font-size: 20px;
	font-weight: 300;
	color: #777;
	margin-bottom: 10px;
}
